            _balances[bondAccount] = _balances[bondAccount]
                .sub(signedRewardAmount)
                .sub(signedBailoutAmount);
            // - penalty applies (excluding the bailout)
            _balances[penaltyAccount] = _balances[penaltyAccount]
                .add(signedBailoutAmount);
